enumerate = () => {
  return {
    name: "enumerate",
    tooltip: "Enumeration",
    exec: (editor) => {

      let content = editor.value;
      // Removes tabs, newlines, and extra whitespaces
      content = content.replace(/[\t\n\r ]+/g, " ");    
      // Removes non-breaking spaces
      content = content.replace(/\u00a0/g, " ");
      // Updates the editor's value
      editor.value = content;


        //           /.*?(?<! Mr| Mrs| Ms| ca| Chr| u\.U| o\.Ä| usw| usf| z\.B| NB| e\.g| bzw| z\.Z| evtl| resp| Anm| \(Anm| d\.h| e\.V| geb| Jahrh| lat| m\.E| n\.V| u\.a| z\.T| i\.e| etc| jr| jun| a\.m| p\.m)[.!?\u2026](?=(?<!\d\.|\d\s\.)\s+|\s*$)/gi;
        const regP =
          /.*?(?<! rd| v| d| Tab| vs| dr| engl| nb| d\.h| s| pp| p| Erkl| z\.z| bzw| dr| u\.a| Anm| \(Anm| e\.g| Mr| Mrs| Ms| resp| i\.e| z\.B| u\.U| o\.Ä| ca| Chr| usw| usf| evtl| e\.V| Jahrh| lat| m\.E| n\.V| z\.T| etc| jr| jun| a\.m| p\.m)[.!?\u2026](?=(?<!\d\.|\d\s\.)\s+|<br>|\s*$)/gi;

      
      const hasDecision = editor.container.querySelectorAll(".decision");
      if (hasDecision.length) {
        alert('There are still abbreviation decision.\nMake sure there are no more decisions to be made.')
        return;
      }

      const metaData = editor.container.querySelector("#meta-data");
      let metaDataEl = "";


      // given 2 string references and a count
      function enumerator(countRef, ref1, ref2, innCount = false) {

        //hard coded
        const r1Postfx = "en";
        const r2Postfx = "de";
        const prefx = "track";
        const missing = "--missing"

        let ref1htm = "";
        let ref2htm = "";
        let i = 0;

        let innerCount = (innCount != false)? `_${innCount}`: ``;
        
        
        // matches the string with the pattern regex
        // const ref1Matches = cleanHtml(ref1.innerHTML).replace(/[\t\n\r]/g, '').trim().match(regP);
        // const ref2Matches = cleanHtml(ref2.innerHTML).replace(/[\t\n\r]/g, '').trim().match(regP);

        const ref1Matches = (ref1.innerHTML).trim().match(regP);
        const ref2Matches = (ref2.innerHTML).trim().match(regP);

        // solution for cutoffs ref1
        if (ref1Matches && ref1Matches.length) {
          const ref1MatLen = ref1Matches.join('').length ?? 0;
          let ref1LeftOver = ref1.innerHTML.slice(ref1MatLen);
          if (ref1LeftOver.trim().length != 0) {
            ref1Matches.push(ref1LeftOver);
          }
        }
        
        // solution for cutoffs ref2        
        if (ref2Matches && ref2Matches.length) {
          const ref2MatLen = ref2Matches.join('').length ?? 0;
          let ref2LeftOver = ref2.innerHTML.slice(ref2MatLen);
          if (ref2LeftOver.trim().length != 0) {
            ref2Matches.push(ref2LeftOver);
          }
        }

        // rebuild the nodeValue here
        try {
          longestLen = ref1Matches.length > ref2Matches.length? ref1Matches.length: ref2Matches.length;
          while (i < longestLen) {
            const cntR1 = `${prefx}_${countRef}${innerCount}_${i}-${r1Postfx}`;      
            const cntR2 = `${prefx}_${countRef}${innerCount}_${i}-${r2Postfx}`;

            if (ref1Matches[i] && ref2Matches[i]) {
              ref1htm += `<track-span id="${cntR1}" class="eNum">${ref1Matches[i]}</track-span>`;
              ref2htm += `<track-span id="${cntR2}" class="eNum">${ref2Matches[i]}</track-span>`;
            } else if (!ref1Matches[i] && ref2Matches[i]) {
              ref1htm += `<track-span id="${cntR1}" class="eNum missing">${missing}</track-span>`;
              ref2htm += `<track-span id="${cntR2}" style="color:red;" class="eNum missing">${ref2Matches[i]}</track-span>`;
            } else if (ref1Matches[i] && !ref2Matches[i]) {
              ref1htm += `<track-span id="${cntR1}" style="color:red;" class="eNum missing">${ref1Matches[i]}</track-span>`;
              ref2htm += `<track-span id="${cntR2}" class="eNum missing">${missing}</track-span>`;
            }

            i++;
          }
        } catch (err) {
          console.log("Message: " + err);
        }

        // if there are no regP matches
        try {
          const cntR1 = `${prefx}_${countRef}${innerCount}_${i}-${r1Postfx}`;      
          const cntR2 = `${prefx}_${countRef}${innerCount}_${i}-${r2Postfx}`;
          // if there were no matches just retun org html
          if (!ref1Matches || !ref2Matches) {
            ref1htm = `<track-span id="${cntR1}" class="eNum" >${ref1.innerHTML}</track-span>`;
            ref2htm = `<track-span id="${cntR2}" class="eNum" >${ref2.innerHTML}</track-span>`;
          }
        } catch (err) {}


        ref1.innerHTML  = ref1htm;
        ref2.innerHTML  = ref2htm;

      } 

      function nested_table(curCount, cell1Table, cell2Table ) {
        innerTblCnt = 0;

        const $engNested = cell1Table.querySelectorAll("tr");        
        const $gerNested = cell2Table.querySelectorAll("tr");


        // no check to make sure the tds are the same
       // loop through the tds 
       $gerNested.forEach((element, ind) => {
        // console.log(element.querySelectorAll("td"));
        rightTds = $gerNested[ind].querySelectorAll("td")
        leftTds = $engNested[ind].querySelectorAll("td")
        
        rightTds.forEach((ele, ind) => {
            // get the text 

            cellRef1 = cleanHtml(leftTds[ind].innerHTML);
            cellRef2 = cleanHtml(rightTds[ind].innerHTML);
      
            enumerator(curCount, cellRef1, cellRef2, innerTblCnt);
           
            // set value to enum segments            
            innerTblCnt++;
          }); 

       })

      }

      // two table col translate 
      function format_FOMK(tarHtml, curCount = 1, $isTable = false) {
        const cellRef1 = tarHtml.querySelector(":scope > td:first-child");
        const cellRef2 = tarHtml.querySelector(":scope > td:last-child");

        let isInnerTableRef1 = "";
        let isInnerTableRef2 = "";

        try {
          isInnerTableRef1 = cellRef1.querySelector("table"); 
          isInnerTableRef2 = cellRef2.querySelector("table");

          if (isInnerTableRef1) {
            nested_table(curCount, isInnerTableRef1, isInnerTableRef2 )
            return; // tarHtml;
          }
 
        } catch (error) {
           return; // tarHtml;
        }

        enumerator(curCount, cellRef1, cellRef2);

      }

      // this will check what we have
      if (metaData && metaData.dataset.state === "enumerated") {
        alert("Enumeration already present \nPlease run remove enumeration first");
        return;
      } else if (metaData && metaData.dataset.state !== "enumerated") {
        metaData.dataset.state = "enumerated";
      } else {
        metaDataEl = `<input type="hidden" id="meta-data" data-format="FMOK" data-state="enumerated" data-visable="true" style="display:none"></input>`;
        editor.value += metaDataEl; 
      }

      // get only the first children 
      const mainTable = editor.container.querySelector("table")
      const nodeList = mainTable.querySelectorAll(":scope > tbody > tr");

      for (let i = 0; i < nodeList.length; i++) {
          format_FOMK(nodeList[i], i)
      }

    },
  };
};

removeEnumerate = () => {
  return {
    name: "Renumerate",
    tooltip: "Removes Enumeration",
    exec: (editor) => {
      const removeSpan = function (tarHtml) {
        $engStr = tarHtml.querySelector(":scope > td:first-child").innerHTML;
        $gerStr = tarHtml.querySelector(":scope > td:last-child").innerHTML;

        // const regex =/(?:<track-span id="track.*?" class="(?:eNum|toHide).*?"\s*?>)([\w\W]*?)(?:<\/track-span>)/g;
        const regex =/(?:<track-span id="track.*?" *?>)([\w\W]*?)(?:<\/track-span>)/g;
        
        // removes any left over tags 
        const regex2 =/<track-span id="track.*?" *?>/g;
        const regex3 =/<\/track-span>/g;

        let eng = $engStr.replaceAll(regex, function(i, match) {
          if (match == "--missing")
            return "";
          return match;
        }).replaceAll(regex2, "").replaceAll(regex3, "");

        let ger = $gerStr.replaceAll(regex, function(i, match) {
          if (match == "--missing")
            return "";
          return match;
        }).replaceAll(regex2, "").replaceAll(regex3, "");

        tarHtml.querySelector(":scope > td:first-child").innerHTML = eng;
        tarHtml.querySelector(":scope > td:last-child").innerHTML = ger;

        return tarHtml;
      };

      const metaData = editor.container.querySelector("#meta-data");

      if (!metaData || metaData.dataset.state !== "enumerated") {
        alert("no enumeration present");
        return;
      } else {
        metaData.dataset.state = "not enumerated";
      }

      const mainTable = editor.container.querySelector("table");

      const nodeList = mainTable.querySelectorAll(":scope > tbody > tr");
      htmlRtn = document.createElement("table");

      for (let i = 0; i < nodeList.length; i++) {
        htmlRtn.appendChild(removeSpan(nodeList[i]));
      }

      editor.value = cleanHtml(htmlRtn.outerHTML);
    },
  };
};


const highlightAbbreviationsPublication = () => {
  return {
    name: "descisionB",
    tooltip: "Fix Abbreviations",
    exec: (editor) => {     

      const hasDecision = editor.container.querySelectorAll(".decision");
      if (hasDecision.length) {
        alert("There are still abbreviation decision.\nMake sure there are no more decisions to be made.")
        return;
      }

      const abbreviations = [ " Chr.", " usw.", " usf.", " e.V.", " Jahrh.", " lat.", " m.E.", 
                             " n.V.", " z.T.", " etc.", " jr.", " jun.", " a.m.", " p.m."];
     

      let masterString = htmlEncode(editor.container.querySelector(".jodit-wysiwyg").innerHTML);

      abbreviations.reverse().forEach(abbr => {
        const replacement = `
          <decision-span class="decision">
            <decision-span class="decide" onclick="decision(event)" >${abbr}</decision-span>
            <decision-span class="decide-true" onclick="decision(event)" >${abbr}.</decision-span>
          </decision-span>
        `;

        masterString = masterString.replaceAll(abbr, replacement);
      });

      editor.value = htmlDecode(masterString);
    }
  }
}

nextDiscrepancy = () => {
  return {
    name: "ScrollDis",
    tooltip: "Scroll discrepancy into view",
    exec: (editor) => {
      if (editor.container.querySelectorAll(".decision").length) {
        editor.container.querySelector(".decision")
          .scrollIntoView({behavior: "smooth", block: "center", inline: "nearest"});
        return;
      }
     editor.container.querySelector(".missing")
      .scrollIntoView({behavior: "smooth", block: "center", inline: "nearest"});
    }
  }

}

const decision = (e) => {
  const text = e.target.textContent;
  const parent = e.target.parentNode;
  const parentParent = parent.parentNode;

  // Create a new text node with the selected abbreviation
  const newText = document.createTextNode(text);

  // Replace the decision span with the new text node
  parentParent.replaceChild(newText, parent);

  // After modifying the document, normalize it
  parentParent.normalize();
  
  parentParent.parentNode.textContent.replace(/[\t\n\r]/g, '')
  // console.log()
};


function getTableCellContentAndNormalize(editor) {
  const currentNode = editor.s.current();

  if (currentNode) {
      let tableCellNode = currentNode;
      
      // Traverse the DOM tree upwards to find the table cell node
      while (tableCellNode && tableCellNode.nodeName !== 'TD' && tableCellNode.nodeName !== 'TH') {
          tableCellNode = tableCellNode.parentNode;
      }
      
      // If the table cell node is found, normalize its content and return the innerHTML
      if (tableCellNode) {
          tableCellNode.normalize();
          return tableCellNode.innerHTML;
      }
  }

  return null;
}


